function formatDate(value) {
  const date = new Date(value);
  return `${date.getFullYear()}. ${date.getMonth() + 1}. ${date.getDate()}`;
}

function afterCurrentDate(value) {
  let current = new Date();
  console.log(current);
  let createdAt = new Date(value);
  console.log(createdAt);
  let timeDiff = createdAt - current;
  let timeDiffMin = timeDiff / 1000 / 60;
  let timeDiffHour = timeDiff / 1000 / 60 / 60;
  let timeDiffDay = timeDiff / 1000 / 60 / 60 / 24;
  let timeDiffMonth = timeDiff / 1000 / 60 / 60 / 24 / 31;
  let timeDiffYear = timeDiff / 1000 / 60 / 60 / 24 / 31 / 12;
  if (timeDiffMin < 2) return "1 minutes ago";
  if (timeDiffMin < 60) return `${timeDiffMin} minutes ago`;
  if (timeDiffMin < 24 * 60) return `${timeDiffHour} hours ago`;
  if (timeDiffMin < 25 * 60) return "1 day ago";
  if (timeDiffMin < 31 * 60) return `${timeDiffDay} days ago`;
  if (timeDiffMin < 61 * 60) return "1 month ago";
  if (timeDiffMin < 11 * 31 * 60) return `${timeDiffMonth} months ago`;
  if (timeDiffMin < 2 * 12 * 31 * 60) return "1 year ago";
  if (2 * 12 * 31 * 60 <= timeDiffMin) return `${timeDiffYear} years ago`;
}

function LinkListItem({ item }) {
  return (
    <div>
      <img src={item.imageSource} alt={item.title} />
      <div>
        <p>{afterCurrentDate(item.createdAt)}</p>
        <h3>{item.description}</h3>
        <p>{formatDate(item.createdAt)}</p>
      </div>
    </div>
  );
}

export function LinkList({ items }) {
  return (
    <ul>
      {items.map((item) => {
        return (
          <li key={item.id}>
            <LinkListItem item={item} />
          </li>
        );
      })}
    </ul>
  );
}
