import "./style.css";
import logoImg from "../img/logo.png";
import { HeaderApi } from "../api";
import { useEffect, useState } from "react";

function Header() {
  const [userData, setUserData] = useState(null);
  const [userProfile, setUserProfile] = useState({
    img: "",
    email: "",
  });

  const user = async () => {
    try {
      const result = await HeaderApi();
      setUserData(result);
      setUserProfile((prev) => ({
        ...prev,
        [userProfile.img]: result.profileImageSource,
      }));
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    user();
  }, []);

  return (
    <header>
      <div className="header_inner">
        <a href="/">
          <h1>
            <img src={logoImg} alt="logo" />
          </h1>
        </a>

        {userData ? (
          <p>{`${userProfile.img}`}</p>
        ) : (
          <a href="/signin">로그인</a>
        )}
      </div>
    </header>
  );
}

export default Header;
